<script type="text/javascript">
	function getParameterByName(name) {
		name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");
		var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),
				results = regex.exec(location.search);
		return results === null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));
	}
	var chave = getParameterByName('publicKey');
  	var notificationCode = getParameterByName('notificationCode');
    var appType = jQuery.cookie('appType');
	if(chave !== "")
	{
      document.getElementById('chave').innerHTML = '<small>Lembre-se: Após instalar o módulo enviado para seu e-mail, configure sua chave em <strong>Formas de Pagamento -> PagSeguro PRO - Licença</strong>. <br/><h6>Sua chave é: '+ chave + '</h6></small>';
	}

  if(appType != null && notificationCode != '') {
    jQuery.post('https://ws.ricardomartins.net.br/pspro/v7/app/notification/' + appType, {
      notificationCode: notificationCode,
      notificationType: 'applicationAuthorization'
    });

  }

  window.addEventListener("message", function (ev) {
    if (ev.data.message === 'getPublicKey') {
      ev.source.postMessage({message: chave}, "*"); 
    }
  });
