    <table class="table table-stripped table-bordered">
        <tbody>
        <tr>
            <th>
                Seu nome:
            </th>
            <td>
                <input type="text" maxlength="30" size="30" id="name" name="name">

            </td>
        </tr>
        <tr>
            <th>
                Telefone:
            </th>
            <td>
                <input type="tel" maxlenght="30" size="30" id="phone" name="phone">
            </td>
        </tr>
        <tr>
            <th>* E-mail do responsável:</th>
            <td>
                <input type="email" size="30" id="email" name="email">
                <small id="email-suggest"></small>
                <small>O e-mail do lojista será pedido no próximo passo, caso não esteja logado no PagSeguro.</small>
            </td>
        </tr>
        <tr>
            <th>* URL da Loja:
            </th>
            <td>
                <input type="url" size="30" id="url" name="url">
                <small>Essa informação não influencia o funcionamento do módulo. Lembre-se de colocar http:// ou
                    https:// na URL.</small></td>
        </tr>
        <tr>
            <th>* Modelo de recebimento:
            </th>
            <td>
                <select name="app_type" id="app_type" onchange="checkInvite()">
                    <!-- <option value="pagseguro-02">Recebimento em 2 dias: 4,99%</option> -->
                    <option value="pagseguro" selected="">Recebimento em 14 dias: 4,15%</option>
                    <option value="pagseguro-30">Recebimento em 30 dias: 3,15%</option>
                    <option value="pagseguro-transparente-invisible">Invisível Antecipado (Exige convite)</option>
                    <option value="pagseguro-transparente-invisible-fluxo">Invisível no Fluxo (Exige convite)</option>
                    <option value="pagseguro-flex-01">Flex 01 (Exige convite)</option>
                </select><br></td>
        </tr>
        <tr id="inviteRow">
            <th>
                Código do Convite:
            </th>
            <td>
                <input type="text" size="30" id="invite_code" name="invite_code"> *
                <br>
                <small>As <a href="https://pagsegurotransparente.zendesk.com/hc/pt-br/articles/360035615752" target="_blank">aplicações invisíveis</a> são restritas à convidados no momento.</small>
            </td>
        </tr>

        <tr>
            <td colspan="2">
              <p>Ao continuar você e o titular da conta concordam com os <a href="/terms.html" target="_blank" draggable="false">Termos de Uso e Privacidade</a> de nossa aplicação.</p>
                <input type="button" onclick="validateAndSubmit();" value="Avançar" class="btn btn-cta-primary"></td>
        </tr>
        </tbody>
    </table>
</form>
<script type="text/javascript">
    if (document.URL.search('local') > 0 || document.URL.search('127.0.0.1') > 0) {
        document.getElementById("formAppNew").action = "https://v7.test/app/new";
    } else {
        document.getElementById("formAppNew").action = "https://ws.ricardomartins.net.br/pspro/v7/app/new";
    }
    var validateAndSubmit = function () {
        var email = document.getElementById("email").value;
        var url = document.getElementById("url").value;
        var inviteCode = document.getElementById("invite_code").value;
        var appType = document.getElementById("app_type").value;
        if (!validateEmail(email)) {
            alert('Email inválido.');
            return false;
        }
        if (!validateUrl(url)) {
            alert('URL inválido.');
            return false;
        }
        if (appType.indexOf('pagseguro-transparente-invisible') >= 0 && inviteCode == '') {
            alert('Informe o código de convite.');
            return false;
        }

      	setCookie('appType', appType, 10);
        document.getElementById("formAppNew").submit();
    }

    var setCookie = function(cname, cvalue, exmin) {
      const d = new Date();
      d.setTime(d.getTime() + (exmin*60*1000));
      let expires = "expires="+ d.toUTCString();
      document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }
    var validateEmail = function (email) {
        var re = new RegExp('(([^<>()[\\]\\\\.,;:\\s@@' + '"]+(\\.[^<>()[\\]\\\\.,;:\\s@@' + '"]+)*)|(".+"))@@' + '((\\[[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}\\])|(([a-zA-Z\\-0-9]+\\.)+[a-zA-Z]{2,}))');
        return re.test(email);
    }
    var validateUrl = function (url) {
        var re = new RegExp("^(?:(?:https?|ftp):\\/\\/)(?:\\S+(?::\\S*)?@@'+')?(?:(?!10(?:\\.\\d{1,3}){3})(?!127(?:\\.\\d{1,3}){3})(?!169\\.254(?:\\.\\d{1,3}){2})(?!192\\.168(?:\\.\\d{1,3}){2})(?!172\\.(?:1[6-9]|2\\d|3[0-1])(?:\\.\\d{1,3}){2})(?:[1-9]\\d?|1\\d\\d|2[01]\\d|22[0-3])(?:\\.(?:1?\\d{1,2}|2[0-4]\\d|25[0-5])){2}(?:\\.(?:[1-9]\\d?|1\\d\\d|2[0-4]\\d|25[0-4]))|(?:(?:[a-z\\u00a1-\\uffff0-9]+-?)*[a-z\\u00a1-\\uffff0-9]+)(?:\\.(?:[a-z\\u00a1-\\uffff0-9]+-?)*[a-z\\u00a1-\\uffff0-9]+)*(?:\\.(?:[a-z\\u00a1-\\uffff]{2,})))(?::\\d{2,5})?(?:\\/[^\\s]*)?$", "i");
        return re.test(url);
    }

    var getUrlVars = function (sParam) {
        var sPageURL = window.location.search.substring(1);
        var sURLVariables = sPageURL.split('&');
        for (var i = 0; i < sURLVariables.length; i++) {
            var sParameterName = sURLVariables[i].split('=');
            if (sParameterName[0] == sParam) {
                return sParameterName[1];
            }
        }
    }

    var checkInvite = function () {
        var appType = document.getElementById("app_type").value;
        var inviteRow = document.getElementById("inviteRow");

        if (jQuery('#app_type').prop('selectedIndex') > 1) {
            jQuery(inviteRow).show();
        } else {
            jQuery(inviteRow).hide();
        }
    }
    var appVar = getUrlVars('app');
