<script>
  function handleSubmit() {
  var form = jQuery('form[name="resend-module"]');
  var msgObj = jQuery('#msgResponse').first();
	var imgLoad = jQuery('form[name="resend-module"] img').first();
  var formData = form.serialize();
    var postURL = (document.URL.search('local') > 0 || document.URL.search('127.0.0.1') > 0) ? 'https://v7.test/resend' : 'https://ws.ricardomartins.net.br/pspro/v7/resend';
  imgLoad.show();
  jQuery.ajax({
 		type: 'POST',
		url: postURL,
//     dataType: 'json',
    data: formData,
    beforeSend: function(){imgLoad.fadeIn(); msgObj.hide();},
    complete: function(data){
      var response = data.responseJSON;
      if(undefined != response.error){
        msgObj.html(response.error.message);
        msgObj.css('color', 'red');
        msgObj.show();
      }

      if(undefined != response.message){
        msgObj.html(response.message);
        msgObj.css('color', 'black');
        msgObj.show();
        console.log('success');

      }
      imgLoad.fadeOut();
    }

  });
}
jQuery('form[name="resend-module"] img').hide();
jQuery('form[name="resend-module"]').submit(function( event ) {
  handleSubmit();
  event.preventDefault();
});
