<div class="slidecontainer">
  <div class="half">
    <p>Pedidos/mês: <span id="pedidosCur">5</span></p>
    <input type="range" min="5" max="2000" value="50" class="slider" id="pedidosMes">
  </div>
  <div class="half">
    <p>Faturamento: <span id="faturamentoCur">R$ 10.000,00</span></p>
    <input type="range" min="300" max="750000" value="10000" class="slider" id="faturamentoMes">
  </div>
  <p class="economia">Você economiza <span id="economia" class="econRes">R$ 104,00</span> por mês ou <span id="economiaAno" class="econRes">R$ 1.248,00</span> por ano.</p>
</div>

<style>
.slidecontainer {
  width: 100%;
  clear: both;
  font-size: 16px;
}
.half{
  width: 40%;
  min-width: 300px;
  float: left;
  padding: 5px;
}

.slidecontainer .economia{
  clear: both;
  font-size: 18px;
}
.econRes {font-size: 20px; font-weight: bold;}
/* The slider itself */
.slider {
  -webkit-appearance: none;  /* Override default CSS styles */
  appearance: none;
  width: 100%; /* Full-width */
  height: 25px; /* Specified height */
  background: #d9d9d9; /* Grey background */
  outline: none; /* Remove outline */
  opacity: 0.7; /* Set transparency (for mouse-over effects on hover) */
  -webkit-transition: .2s; /* 0.2 seconds transition on hover */
  transition: opacity .2s;
}

/* Mouse-over effects */
.slider:hover {
  opacity: 1; /* Fully shown on mouse-over */
}

/* The slider handle (use -webkit- (Chrome, Opera, Safari, Edge) and -moz- (Firefox) to override default look) */
.slider::-webkit-slider-thumb {
  -webkit-appearance: none; /* Override default look */
  appearance: none;
  width: 25px; /* Set a specific slider handle width */
  height: 25px; /* Slider handle height */
  background: #8AAD5D; /* Green background */
  cursor: pointer; /* Cursor on hover */
}

.slider::-moz-range-thumb {
  width: 25px; /* Set a specific slider handle width */
  height: 25px; /* Slider handle height */
  background: #8AAD5D; /* Green background */
  cursor: pointer; /* Cursor on hover */
}
</style>

<script type="text/javascript">
var pedidos = document.getElementById("pedidosMes");
var faturamento = document.getElementById("faturamentoMes");
var pedidosCur = document.getElementById("pedidosCur");
var faturamentoCur = document.getElementById("faturamentoCur");
var economia = document.getElementById('economia');
var economiaAno = document.getElementById('economiaAno');

pedidosVal = pedidos.value;
faturamentoVal = faturamento.value;

pedidos.oninput = function() {
  pedidosVal = this.value;
  pedidosCur.innerHTML = this.value;
  economia.innerHTML = calcEconomia(1);
  economiaAno.innerHTML = calcEconomia(12);
}

faturamento.oninput = function() {
  faturamentoVal = this.value;
  faturamentoCur.innerHTML = new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(this.value);
  economia.innerHTML = calcEconomia(1);
  economiaAno.innerHTML = calcEconomia(12);
}

function calcEconomia(meses){
  let econ = ((pedidosVal * 0.4) + (faturamentoVal * 0.0084)) * meses;
  console.log(pedidosVal, faturamentoVal, meses);
  return new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(econ);
}
