  // Obtém o elemento HTML
var countdownElement = document.getElementById('timer');

// Define o tempo inicial (10 minutos)
var timeLeft = 10*60;

// Atualiza a contagem regressiva a cada segundo
var countdownTimer = setInterval(function() {
    // Calcula os minutos e segundos restantes
    var minutes = Math.floor(timeLeft / 60);
    var seconds = timeLeft % 60;

    // Atualiza o texto do elemento HTML
    countdownElement.textContent = minutes.toString().padStart(2, '0') + ' minutos e ' + seconds.toString().padStart(2, '0') + ' segundos';

    // Se a contagem regressiva chegou a zero
    if (timeLeft <= 0) {
        // Para a atualização da contagem regressiva
        clearInterval(countdownTimer);

        // Redireciona o usuário para outra página
        window.location.href = 'https://pagseguro.ricardomartins.net.br/connect/autorizar.html';
    }

    // Decrementa o tempo restante
    timeLeft--;
}, 1000);
</script>","additionalCss":"","constructorName":"CustomHtml","packageData":{"id":4276860,"name":"Text","userId":60381,"version":"2.9.2","buildSourceHash":"4181520743"},"id":10,"dataId":2948896,"css":{}},"bd-textblock132":{"textTag":"p","content":"⚠️ <b>Importante:</b> Ao iniciar o processo de autorização acima, faça login no PagBank usando a <b>conta principal</b> e não subcontas.","constructorName":"TextBlock","packageData":{"id":4276860,"name":"Text","userId":60381,"version":"2.9.2","buildSourceHash":"3446640151"},"id":132,"dataId":2958686,"css":{},"effects":{"0":{"constructorName":"ContainerEffect","packageData":{"id":4581237,"name":"Common","userId":60381,"version":"4.15.5","buildSourceHash":"1719862485"},"id":7,"dataId":2958688,"css":{}},"length":1,"constructorName":"EffectsCollection","packageData":{"id":0,"name":"BaseCollections","userId":60381,"version":"0.0.1","buildSourceHash":"542005542"},"id":115,"dataId":2958689}},"bd-textblock31":{"textTag":"p","content":"Seja WooCommerce ou Magento, temos uma loja de exemplo pra você testar nossas integrações PagBank (PagSeguro).<br>As credenciais de admin encontram-se em cada um dos links abaixo.","constructorName":"TextBlock","packageData":{"id":4276860,"name":"Text","userId":60381,"version":"2.9.2","buildSourceHash":"3446640151"},"id":31,"dataId":2968457,"css":{}},"bd-linkbutton5":{"button":{"constructorName":"Style","typeCategory":"Button","mixinName":"","dataIds":{"":2968708,"hovered":2968718,"passive":2968719,"pressed":2968720}},"icon":{"passive":{"icon":"","align":"before","picture":{"mediaId":"","fileName":"","isNone":false,"constructorName":"Picture","packageData":{"id":0,"name":"BasePrimitives","userId":60381,"version":"0.0.1","buildSourceHash":"1819438063"},"dataId":2968709},"constructorName":"IconState","packageData":{"id":4581237,"name":"Common","userId":60381,"version":"4.15.5","buildSourceHash":"683913626"},"dataId":2968710,"css":{}},"hovered":{"icon":"","align":"before","picture":{"mediaId":"","fileNam